import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleDoubleUp } from "@fortawesome/free-solid-svg-icons";
import starOutline from "../images/star-outline.svg";

const Posts = ({ posts, filtered, handleDelete }) => {
  return (
    <div id="entryWrapper">
      <div id="dateOrder">
        <FontAwesomeIcon icon={faAngleDoubleUp} />
        <p>DATE</p>
      </div>
      {filtered.map((post) => {
        return (
          <div className="entry" key={post.date}>
            <p className="entryDate">{post.date}</p>
            <p className="entryContent">{post.content}</p>
            <img
              src={starOutline}
              alt=""
              className="star"
              name={post.content}
              id={post.id}
              onClick={handleDelete}
            />
            <img src="./images/edit.svg" alt="" id="edit" />
            <img src="./images/trash.svg" alt="" id="trash" />
          </div>
        );
      })}
      <div className="entry"></div>
    </div>
  );
};

export default Posts;
