import { useState, useEffect } from "react";
import postServices from "./services/postServices";

import Filter from "./components/filter";
import PostForm from "./components/postForm";
import Posts from "./components/posts";
import sun from "./images/sun.svg";
import week from "./images/week.png";
import month from "./images/month.png";
import star from "./images/star.png";

const App = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    postServices.getAll().then((response) => {
      setPosts(response.data);
    });
  }, []);

  const [filter, setFilter] = useState("");
  const [newContent, setNewContent] = useState("");
  const [newDate, setNewDate] = useState("");
  const [successMessage, setSuccessMessage] = useState(null);

  const handleFilter = (event) => {
    setFilter(event.target.value);
  };

  const handleNewPost = (event) => {
    setNewContent(event.target.value);
  };

  const handleNewDate = (event) => {
    setNewDate(event.target.value);
    console.log(newDate);
  };

  const addNewPost = (event) => {
    const newDateConv = new Date(newDate);
    const options = {
      year: "numeric",
      month: "short",
      day: "numeric",
    };
    const newPost = {
      content: newContent,
      date: newDateConv.toLocaleDateString("en-us", options),
      id: posts.length + 1,
    };
    const postToChange = posts.find((post) => post.date === newDate);
    const changedPost = { ...postToChange, content: newContent };
    event.preventDefault();
    /* 
    if (posts.map((post) => post.date === newDate)) {
      if (
        window.confirm(
          "There is already an existing entry for " +
            newDate +
            ". Edit the content for this date?"
        ) === true
      ) {
        postServices
          .update(postToChange.id, changedPost)
          .then((response) => {
            return response.data;
          })
          .catch((error) => {
            setSuccessMessage(
              `Entry for ${newDate} has already been removed from the server`
            );
            setTimeout(() => {
              setSuccessMessage(null);
            }, 5000);
          });
        postServices.getAll().then((response) => {
          setPosts(response.data);
        });
        setSuccessMessage(`Updated the entry for ${newDate}`);
        setTimeout(() => {
          setSuccessMessage(null);
        }, 5000);
      } else {
        return;
      }

    } else { */
    setPosts(posts.concat(newPost));
    setSuccessMessage(`Added an entry for ${newDate}`);
    setTimeout(() => {
      setSuccessMessage(null);
    }, 5000);
    setNewContent("");
    setNewDate("");

    postServices.create(newPost).then((response) => {
      setPosts(posts.concat(response.data));
    });
  };

  const filtered = posts.filter((post) => {
    if (post.content === undefined || filter === undefined) {
      return;
    } else {
      return post.content.toLowerCase().includes(filter.toLowerCase());
    }
  });

  const handleDelete = (event) => {
    const idDelete = event.target.id;
    const postDelete = event.target.date;
    console.log(postDelete);

    if (
      window.confirm(
        `Are you sure you want to delete the entry for ${postDelete}?`
      )
    ) {
      postServices.deletePost(idDelete).then((response) => {
        return response.data;
      });
      postServices.getAll().then((response) => {
        setPosts(response.data);
      });
      setSuccessMessage(`Deleted the entry for ${postDelete}`);
      setTimeout(() => {
        setSuccessMessage(null);
      }, 5000);
    }
  };

  return (
    <div>
      <div id="header">
        <div class="logo">
          <img src={sun} alt="" id="sun" />
          <h1 class="title">Gratitude Journal</h1>
        </div>
        <Filter filter={filter} handleFilter={handleFilter} />
      </div>
      <div id="main">
        <div id="sidebar">
          <div class="sbHeader">HOME</div>
          <hr />
          <div class="sbitems">
            <img src={week} alt="" class="svgicon" />
            <p>LAST 7 DAYS</p>
          </div>
          <div class="sbitems">
            <img src={month} alt="" class="svgicon" />
            <p>LAST 30 DAYS</p>
          </div>
          <div class="sbitems">
            <img src={star} alt="" class="svgicon" />
            <p>STARRED</p>
          </div>
          <br />
          <br />
          <div class="sbHeader">ARCHIVE</div>
          <hr />
          <p href="" class="archiveItems">
            2023
          </p>
          <p href="" class="archiveItems">
            2022
          </p>
        </div>
        <div id="content">
          <div class="titleBox">
            <p>Last 7 Days</p>
          </div>
          /*
          <h2>Gratitude Journal</h2>
          <h2>Add a new post</h2>
          <PostForm
            addNewPost={addNewPost}
            newContent={newContent}
            handleNewPost={handleNewPost}
            newDate={newDate}
            handleNewDate={handleNewDate}
          />
          <Posts
            posts={posts}
            filtered={filtered}
            handleDelete={handleDelete}
          />
          <div class="newPost">
            <button id="newButton" onclick="openPost()">
              &nbsp;&nbsp;+ &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;NEW
            </button>
            <div class="form-popup" id="entryForm">
              <form action="/action_page.php" class="form-container">
                <p>New Gratitude Post</p>
                <div class="dateWrapper">
                  <label for="date">
                    <b>Date:&nbsp;&nbsp;&nbsp;</b>
                  </label>
                  <input type="Date" name="date" required />
                </div>
                <br />
                <div class="postBox">
                  <label for="post">
                    <b>Entry Details:</b>
                  </label>
                  <textarea
                    placeholder="I am grateful for..."
                    name="post"
                    required
                  ></textarea>
                </div>
                <div class="buttonWrapper">
                  <button type="submit" class="btn">
                    Post
                  </button>
                  <button
                    type="button"
                    class="btn cancel"
                    onclick="closePost()"
                  >
                    Close
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
