import { useState, useEffect } from "react";
import postServices from "./services/postServices";

import Filter from "./components/filter";
import PostForm from "./components/postForm";
import Posts from "./components/posts";
import sun from "./images/sun.svg";
import box from "./images/box.png";
import week from "./images/week.png";
import month from "./images/month.png";
import star from "./images/star.png";

/* --- --- --- --- --- VARIABLES --- --- --- --- --- --- */

const App = () => {
  const [posts, setPosts] = useState([]);
  const [categorized, setCategorized] = useState([]);

  useEffect(() => {
    postServices.getAll().then((response) => {
      setPosts(response.data);
      setCategorized(response.data);
    });
  }, []);

  const [filter, setFilter] = useState("");
  const [newContent, setNewContent] = useState("");
  const [newDate, setNewDate] = useState("");
  const [order, setOrder] = useState(true);

  const handleFilter = (event) => {
    setFilter(event.target.value);
  };

  const handleNewPost = (event) => {
    setNewContent(event.target.value);
  };

  const handleNewDate = (event) => {
    setNewDate(event.target.value);
    console.log(newDate);
  };

  const generateId = () => {
    return Math.floor(Math.random() * 9999999999999999);
  };

  /* --- --- --- --- --- FUNCTIONS --- --- --- --- --- --- */

  const addNewPost = (event) => {
    const newDateConv = new Date(newDate);
    const options = {
      year: "numeric",
      month: "short",
      day: "numeric",
    };
    const newPost = {
      content: newContent,
      date: newDateConv.toLocaleDateString("en-us", options),
      id: generateId(),
      favorite: false,
    };
    const postToChange = posts.find((post) => post.date === newDate);
    const changedPost = { ...postToChange, content: newContent };
    event.preventDefault();
    /* 
    if (posts.map((post) => post.date === newDate)) {
      if (
        window.confirm(
          "There is already an existing entry for " +
            newDate +
            ". Edit the content for this date?"
        ) === true
      ) {
        postServices
          .update(postToChange.id, changedPost)
          .then((response) => {
            return response.data;
          })
          .catch((error) => {
            setSuccessMessage(
              `Entry for ${newDate} has already been removed from the server`
            );
            setTimeout(() => {
              setSuccessMessage(null);
            }, 5000);
          });
        postServices.getAll().then((response) => {
          setPosts(response.data);
        });
        setSuccessMessage(`Updated the entry for ${newDate}`);
        setTimeout(() => {
          setSuccessMessage(null);
        }, 5000);
      } else {
        return;
      }

    } else { */
    setPosts(posts.concat(newPost));
    setNewDate("");

    postServices.create(newPost).then((response) => {
      setPosts(posts.concat(response.data));
    });
    setNewContent("");
  };

  const refresher = (event) => {
    setCategorized(posts);
    console.log(posts.length, categorized.length);
    categorizer(event);
  };

  const categorizer = (event) => {
    console.log(posts.length, categorized.length);
    let categorizedPosts = categorized.filter((post) => {
      switch (event.target.id) {
        case "all":
          return post;
        case "week":
          return post.date <= new Date() - 7;
        case "month":
          return post.date <= new Date() - 30;
        case "favorites":
          return post.favorite === true;
        case "2022":
          return post.date.getYear() === 2022;
        case "2023":
          return post.date.getYear() === 2023;
        default:
          return post;
      }
    });
    //setCategorized(categorizedPosts);
    console.log(posts.length, categorized.length);
  };

  const filtered = categorized.filter((post) => {
    if (
      post.content === undefined ||
      filter === undefined ||
      filter === null ||
      post.content === null
    ) {
      return;
    } else {
      return post.content.toLowerCase().includes(filter.toLowerCase());
    }
  });

  const sortOrder = () => {
    setOrder(!order);
    postServices.getAll().then((response) => {
      setPosts(response.data);
    });
  };

  const sorted = filtered.sort((post1, post2) => {
    if (order) {
      return new Date(post1.date) - new Date(post2.date);
    } else {
      return new Date(post2.date) - new Date(post1.date);
    }
  });

  const handleDelete = (event) => {
    const idDelete = event.target.id;
    const postDelete = event.target.name;
    console.log(postDelete);

    if (
      window.confirm(
        `Are you sure you want to delete the entry for ${postDelete}?`
      )
    ) {
      postServices.deletePost(idDelete).then((response) => {
        return response.data;
      });
      postServices.getAll().then((response) => {
        setPosts(response.data);
      });
    }
  };

  const editPost = (event) => {
    const targetDate = event.target.name;
    const inputContent = prompt("New details for this entry:", targetDate);
    const idEdit = Number(event.target.id);
    const postToChange = posts.find((post) => post.id === idEdit);
    const changedPost = { ...postToChange, content: inputContent };

    if (inputContent) {
      postServices.update(idEdit, changedPost).then((response) => {
        return response.data;
      });
      postServices.getAll().then((response) => {
        setPosts(response.data);
      });
    } else {
      return;
    }
  };

  const changeStar = (event) => {
    const idEdit = Number(event.target.id);
    const starEdit = Boolean(event.target.name);
    const postToChange = posts.find((post) => post.id === idEdit);
    const changedPost = { ...postToChange, favorite: !starEdit };

    postServices.update(idEdit, changedPost).then((response) => {
      return response.data;
    });
    postServices.getAll().then((response) => {
      setPosts(response.data);
    });
  };

  /* --- --- --- --- --- APP RENDER --- --- --- --- --- --- */

  return (
    <div>
      <div id="header">
        <div className="logo">
          <img src={sun} alt="" id="sun" />
          <h1 className="title">Gratitude Journal</h1>
        </div>
        <Filter filter={filter} handleFilter={handleFilter} />
      </div>
      <div id="main">
        <div id="sidebar">
          <div className="sbHeader">HOME</div>
          <hr />
          <div className="sbitems">
            <img src={box} alt="" className="svgicon" />
            <button id="all" onClick={categorizer}>
              ALL ENTRIES
            </button>
          </div>
          <div className="sbitems">
            <img src={week} alt="" className="svgicon" />
            <button id="week" onClick={categorizer}>
              LAST 7 DAYS
            </button>
          </div>
          <div className="sbitems">
            <img src={month} alt="" className="svgicon" />
            <button id="month" onClick={categorizer}>
              LAST 30 DAYS
            </button>
          </div>
          <div className="sbitems">
            <img src={star} alt="" className="svgicon" />
            <button id="favorites" onClick={categorizer}>
              STARRED
            </button>
          </div>
          <br />
          <br />
          <div className="sbHeader">ARCHIVE</div>
          <hr />
          <button id="2023" className="archiveItems" onClick={categorizer}>
            2023
          </button>
          <button id="2022" className="archiveItems" onClick={categorizer}>
            2022
          </button>
        </div>
        <div id="content">
          <div className="titleBox">
            <p>Last 7 Days</p>
          </div>
          <Posts
            posts={posts}
            order={order}
            sorted={sorted}
            sortOrder={sortOrder}
            changeStar={changeStar}
            handleDelete={handleDelete}
            editPost={editPost}
          />
          <PostForm
            addNewPost={addNewPost}
            newContent={newContent}
            handleNewPost={handleNewPost}
            newDate={newDate}
            handleNewDate={handleNewDate}
          />
        </div>
      </div>
    </div>
  );
};

export default App;
