const Posts = ({ posts, filtered, handleDelete }) => {
  return (
    <div>
      <ul>
        {filtered.map((post) => {
          return (
            <li key={post.id}>
              {post.date.toLocaleDateString()} {post.content}{" "}
              <button name={post.content} id={post.id} onClick={handleDelete}>
                delete
              </button>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default Posts;
