import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faAngleDoubleDown,
  faAngleDoubleUp,
} from "@fortawesome/free-solid-svg-icons";
import starOutline from "../images/star-outline.svg";
import trash from "../images/trash.svg";
import edit from "../images/edit.svg";

const Posts = ({
  posts,
  sorted,
  sortOrder,
  sortEntries,
  handleDelete,
  editPost,
  newContent,
  setChangedContent,
}) => {
  let sortIcon = sortOrder ? faAngleDoubleUp : faAngleDoubleDown;
  return (
    <div id="entryWrapper">
      <div id="dateOrder">
        <FontAwesomeIcon icon={`${sortIcon}`} onClick={sortOrder} />
        <button onClick={sortOrder}>&nbsp;&nbsp;DATE</button>
      </div>
      {sorted.map((post) => {
        return (
          <div className="entry" key={post.date}>
            <p className="entryDate">{post.date}</p>
            <p className="entryContent">{post.content}</p>
            <img src={starOutline} alt="" id="edit" />
            <img
              src={trash}
              alt=""
              className="star"
              name={post.date}
              id={post.id}
              onClick={handleDelete}
            />
            <img
              src={edit}
              alt=""
              id={post.id}
              name={post.content}
              onClick={editPost}
            />
          </div>
        );
      })}
      <div className="entry"></div>
    </div>
  );
};

export default Posts;
