const PostForm = (props) => {
  return (
    <form onSubmit={props.addNewPost}>
      <div>
        Date:{" "}
        <input
          type="Date"
          value={props.newDate}
          onChange={props.handleNewDate}
        />
      </div>
      <div>
        Content:{" "}
        <textarea
          type="text"
          value={props.newPost}
          onChange={props.handleNewPost}
        />
      </div>
      <div>
        <button type="submit">add</button>
      </div>
    </form>
  );
};

export default PostForm;
