import EditForm from "./editForm";
import { newContent, editPost, handleNewPost } from "./App";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleDoubleUp } from "@fortawesome/free-solid-svg-icons";
import starOutline from "../images/star-outline.svg";
import trash from "../images/trash.svg";
import edit from "../images/edit.svg";

const Posts = ({ posts, filtered, handleDelete }) => {
  return (
    <div id="entryWrapper">
      <div id="dateOrder">
        <FontAwesomeIcon icon={faAngleDoubleUp} />
        <p>DATE</p>
      </div>
      {filtered.map((post) => {
        return (
          <div className="entry" key={post.date}>
            <p className="entryDate">{post.date}</p>
            <p className="entryContent">{post.content}</p>
            <img src={starOutline} alt="" id="edit" />
            <img
              src={trash}
              alt=""
              className="star"
              name={post.content}
              id={post.id}
              onClick={handleDelete}
            />
            <img src={edit} alt="" id="edit" />
            <EditForm
              editPost={editPost}
              newContent={newContent}
              handleNewPost={handleNewPost}
            />
          </div>
        );
      })}
      <div className="entry"></div>
    </div>
  );
};

export default Posts;
