import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faAngleDoubleUp,
  faAngleDoubleDown,
} from "@fortawesome/free-solid-svg-icons";
import starOutline from "../images/star-outline.svg";
import star from "../images/star.svg";
import trash from "../images/trash.svg";
import edit from "../images/edit.svg";

const Posts = ({
  posts,
  order,
  starred,
  sorted,
  sortOrder,
  sortEntries,
  addStar,
  handleDelete,
  editPost,
  newContent,
  setChangedContent,
}) => {
  let sortIcon = order ? (
    <FontAwesomeIcon icon={faAngleDoubleUp} onClick={sortOrder} />
  ) : (
    <FontAwesomeIcon icon={faAngleDoubleDown} onClick={sortOrder} />
  );
  let starIcon = starred ? (
    <img src={star} alt="" className="star" id={post.id} onClick={addStar} />
  ) : (
    <FontAwesomeIcon icon={faAngleDoubleDown} onClick={sortOrder} />
  );
  return (
    <div id="entryWrapper">
      <div id="dateOrder">
        {sortIcon}
        <button onClick={sortOrder}>&nbsp;&nbsp;DATE</button>
      </div>
      {sorted.map((post) => {
        return (
          <div className="entry" key={post.date}>
            <p className="entryDate">{post.date}</p>
            <p className="entryContent">{post.content}</p>
            <img
              src={starOutline}
              alt=""
              className="star"
              id={post.id}
              onClick={addStar}
            />
            <img
              src={trash}
              alt=""
              className="trash"
              name={post.date}
              id={post.id}
              onClick={handleDelete}
            />
            <img
              src={edit}
              alt=""
              id={post.id}
              name={post.content}
              onClick={editPost}
            />
          </div>
        );
      })}
      <div className="entry"></div>
    </div>
  );
};

export default Posts;
